import React,{useState, useRef} from "react";
import "./TODO.css"
function TODO(){
    const [todo , setTodo]=useState("");
    const [list , setList]=useState([]);
    const [edit , setEdit]=useState(true);
    const [ mark, setMark]=useState("");

   
   
    const onButtonClick=(e)=>{
        e.preventDefault();
      setList((list)=>{
const updated=[...list,todo];
console.log(updated);
return updated;
      })    
    };
    
   

  
    const onButton2Click=(e)=>{
      e.preventDefault();
    setTodo("") 
  };
  const onRemoveClick=(i)=>{
  const cleaning=list.filter((items,id)=>{
      return i!==id;
      })
      setList(cleaning);
  }

  const onRemoveAll=()=>{
    setList([])
  };
const onCheck=(i)=>{
//   list.filter((items,id)=>{
//     return i!==id;
//     })
// setMark("checked")
// }
  //let's find index of element
  const element =list.findIndex((elem) => elem.i == i);

  //copy array into new variable
  const newTaskList = [...list];

  //edit our element
  newTaskList[element] = {
    ...newTaskList[element],
    isCompleted: true,
  };

  setList(newTaskList);
};
 
 
  const renderlist=list.map((items, i)=>{
    return (
    <div key={i}>
      <input
      type="text"  defaultValue={items} readOnly={edit} className={mark}/>
     <button 
      onClick={()=> onRemoveClick(i)}>remove </button>
      <button 
      onClick={()=>onCheck(i)}>mark </button>
    </div>
    
    
    )
  });


    return( <div>
        <input
        type="text"
        value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
        onClick={onButtonClick}
        >Submit</button>
        <button type="reset" 
         onClick={onButton2Click}>Reset</button>
       <div>{renderlist}
       </div>
       <button onClick={onRemoveAll}>remover all</button>
        </div>
       
        )
}
export default TODO;
