import React,{useState} from "react";
function TODO(){
    const [todo , setTodo]=useState("");

    const onButtonClick=(e)=>{
        // e.preventDefault();
      
      console.log(todo) 
    };
    const onButton2Click=(e)=>{
        e.preventDefault();
      
      setTodo("")
      console.log("cleared this crap")
    };


    return( <div>
       <form>
        <input
        type="text"
        value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
        onClick={onButtonClick}
        >Submit</button>
        <button type="reset"  onClick={onButton2Click}>Reset</button>
       </form>
       if ({onButtonClick}){
        <div> {todo}</div>
       }
        </div>
        )
}
export default TODO;
