      setTodo(input.value);
      console.log(todo) 
    };

    return( <div>
       <form>
        <input
        type="text"
        // value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
       
        >Submit</button>
       </form>
        </div>
        )
}
export default TODO;
