import React,{useState, useRef} from "react";
function TODO(){
    const [todo , setTodo]=useState("");
    const [list , setList]=useState([]);
    const [edit , setEdit]=useState("readOnly");
   
    const inputRef=useRef(null);

    const onButtonClick=(e)=>{
        e.preventDefault();
      setList((list)=>{
const updated=[...list,todo];
console.log(updated);
return updated;
      })    
    };

  
    const onButton2Click=(e)=>{
      e.preventDefault();
    setTodo("")
    
  };
  const onRemoveClick=(i)=>{
  const cleaning=list.filter((items,id)=>{
      return i!=id;
      })
      setList(cleaning);
  };

  const onRemoveAll=()=>{
    setList([])
  };

  const onEditClick=(e)=>{
    console.log("i am working")
   
    inputref.removeAttribute("readonly")
    // setEdit(false)
    
    // [i]=e.target.value
    // setList()

  }
    
    const renderlist=list.map((items, i)=>{
      return (
      <div key={i}>
        <input
        type="text"  defaultValue={items} readOnly ref={inputRef} />
        {/* // disabled={edit} */}
       
       
        
      
       {/* <p>{items}</p> */}
       <button 
        onClick={()=> onRemoveClick(i)}>remove </button>
         <button 
        onClick={()=> onEditClick(i)}>edit </button>
      </div>
      
      )
    });
   






    return( <div>
       {/* <form> */}
        <input
        type="text"
        value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
        onClick={onButtonClick}
        >Submit</button>
        <button type="reset" 
         onClick={onButton2Click}>Reset</button>
       {/* </form> */}
       <div>{renderlist}
       </div>
       <button onClick={onRemoveAll}>remover all</button>
        </div>
       
        )
}
export default TODO;
