import React,{useState} from "react";
function TODO(){
    const [todo , setTodo]=useState("");
    const [list , setList]=useState([]);
    const [edit , setedit]=useState(true);
    

    const onButtonClick=(e)=>{
        e.preventDefault();
      setList((list)=>{
const updated=[...list,todo];
console.log(updated);
return updated;

      }) 
      
    };

    
    
    const renderlist=list.map((items, i)=>{
      return (
      <div key={i}>
        <input
        value={items}
        disabled={edit}/>
        {/* {items} */}
        <button 
        onClick={()=> onRemoveClick(i)}
        >remove </button>
        <button 
        onClick={onEditClick}
        >edit </button>
          
      </div>
      )
    })

    const onButton2Click=(e)=>{
        e.preventDefault();
      setTodo("")
      
    };
    const onRemoveClick=(i)=>{
    const cleaning=list.filter((items,id)=>{
        return i!=id;
        })
        setList(cleaning);
    };

    const onRemoveAll=()=>{
      setList([])
    };

    const onEditClick=()=>{
      return(
        <input
        value={list}
        disabled={false}/>
      )
      
    };
      



    return( <div>
       {/* <form> */}
        <input
        type="text"
        value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
        onClick={onButtonClick}
        >Submit</button>
        <button type="reset" 
         onClick={onButton2Click}>Reset</button>
       {/* </form> */}
       <div>{renderlist}
       </div>
       <button onClick={onRemoveAll}>remover all</button>
        </div>
       
        )
}
export default TODO;
