import React,{useState,} from "react";
import "./App.css"

function App(){
    const [todo , setTodo]=useState("");
    const [list , setList]=useState([]);


// Add task button 
    const onButtonClick=(e)=>{
        e.preventDefault();
      setList((list)=>{
const updated=[...list,todo];
return updated;
      })    
    };
    
    const renderlist=list.map((items, i)=>{
      return (
      <div key={i}>
        <input
        type="text"  defaultValue={items} readOnly="True"/>
       <button onClick={()=> onRemoveClick(i)}>remove </button>
      </div>
      )
    });
  // clear input field button 
    const onButton2Click=(e)=>{
      e.preventDefault();
    setTodo("") 
  };
// remove individual tasks button 
  const onRemoveClick=(i)=>{
  const cleaning=list.filter((items,id)=>{
      return i!==id;
      })
      setList(cleaning);
  };
// remove all button 
  const onRemoveAll=()=>{
    setList([])
  };

 
    return (
       <div className="main">
        <div className="ui input focus">
        <input type="text" value={todo} placeholder="todo" onChange={(e)=>setTodo(e.target.value)} />
        </div>
        <div>
        <button type="submit" onClick={onButtonClick}>Submit</button>
        <button type="reset" onClick={onButton2Click}>Reset</button>
        </div>
       <div>{renderlist}</div>
       <button onClick={onRemoveAll}>remover all</button>
        </div>
        )
}
export default App;
