  }



  
    
    
  const renderlist=list.map((items, i)=>{
    return (
    <div key={i}>
      <input
      type="text"  defaultValue={items}  onChange={(e)=>setVal(e.target.value)} value={val} readOnly={edit} />
     <button 
      onClick={()=> onRemoveClick(i)}>remove </button>
       <button 
      onClick={()=>onEditClick(i)}> edit </button>
    </div>
    
    )
  });





    return( <div>
        <input
        type="text"
        value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
        onClick={onButtonClick}
        >Submit</button>
        <button type="reset" 
         onClick={onButton2Click}>Reset</button>
       <div>{renderlist}
       </div>
       <button onClick={onRemoveAll}>remover all</button>
        </div>
       
        )
}
export default TODO;
