import React,{useState, useRef} from "react";
function TODO(){
    const [todo , setTodo]=useState("");
    const [list , setList]=useState([]);
    const [edit , setEdit]=useState(true);
    const [save , setSave]=useState("Edit");
   
   
    const onButtonClick=(e)=>{
        e.preventDefault();
      setList((list)=>{
const updated=[...list,todo];
console.log(updated);
return updated;
      })    
    };
    const renderlist=list.map((items, i)=>{
      return (
      <div key={i}>
        <input
        type="text"  defaultValue={items} readOnly={edit} />
       <button 
        onClick={()=> onRemoveClick(i)}>remove </button>
         <button 
        onClick={()=> onEditClick(i)}> {save} </button>
      </div>
      
      )
    });
   

  
    const onButton2Click=(e)=>{
      e.preventDefault();
    setTodo("")
    
  };
  const onRemoveClick=(i)=>{
  const cleaning=list.filter((items,id)=>{
      return i!=id;
      })
      setList(cleaning);
  };

  const onRemoveAll=()=>{
    setList([])
  };

  const onEditClick=(e)=>{
    // console.log("i am working")
    // 
    // setSave("Save")
    if ({save}="edit"){
      setEdit(false)
      setSave("save")
    }
  }


  
    
    






    return( <div>
       {/* <form> */}
        <input
        type="text"
        value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
        onClick={onButtonClick}
        >Submit</button>
        <button type="reset" 
         onClick={onButton2Click}>Reset</button>
       {/* </form> */}
       <div>{renderlist}
       </div>
       <button onClick={onRemoveAll}>remover all</button>
        </div>
       
        )
}
export default TODO;
