import React,{useState} from "react";
function TODO(){
    const [todo , setTodo]=useState("");
    const [list , setList]=useState([]);
    

    const onButtonClick=(e)=>{
        e.preventDefault();
      setList((list)=>{
const updated=[...list,todo];
console.log(updated);
return updated;

      }) 
      
    };

    
    
    const renderlist=list.map((items, i)=>{
      return (
      <div key={i}>
        {items}
        <button 
        onClick={()=> onRemoveClick(i)}
        >remove </button>
          
      </div>
      )
    })

    const onButton2Click=(e)=>{
        e.preventDefault();
      setTodo("")
      
    };
    const onRemoveClick=(i)=>{
      // e.preventDefault();
     
      const cleaning=list.filter((items,id)=>{
        return i!=id;
        })
        setList(cleaning);
    
    }

    return( <div>
       {/* <form> */}
        <input
        type="text"
        value={todo}
        placeholder="todo"
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button
        type="submit"
        onClick={onButtonClick}
        >Submit</button>
        <button type="reset" 
         onClick={onButton2Click}>Reset</button>
       {/* </form> */}
       <div>{renderlist}
       </div>
       <button>remover all</button>
        </div>
       
        )
}
export default TODO;
